@app.route('/s_query/<submitted>')
def s_query(submitted = None):
    global data 
    global total_data 
    global rights
    global Genres
    global Utilities
    global Authors

    #json_data = request.get_json() 
    #submitted =  json_data["text"] 
    print("Here is my submitted: ", submitted)
    print(len(submitted))
    # add new entry to array with 
    # a new id and the name the user sent in JSON
    count = 0
    matched = [s for s in rights if submitted.lower() in s.lower()]

    rights_ID = []
    for i in matched:
        rights_ID.append(rights.index(i))
        count += 1


    count = 0
    matched = [s for s in Genres if submitted.lower() in s.lower()]

    genres_ID = []
    for i in matched:
        genres_ID.append(Genres.index(i))
        count += 1

    count = 0
    matched = [s for s in Utilities if submitted.lower() in s.lower()]

    utilities_ID = []
    for i in matched:
        utilities_ID.append(Utilities.index(i))
        count += 1 

    count = 0
    matched = [s for s in Authors if submitted.lower() in s.lower()]

    authors_ID = []
    for i in matched:
        authors_ID.append(Authors.index(i))
        count += 1   
    


    authors_data_ID = []
    utilities_data_ID = []
    genres_data_ID = []

    for i in range(total_data):
        arr_temp_a = data[str(i+1)]["Author"]
        arr_temp_u = data[str(i+1)]["Utility"]
        arr_temp_g = data[str(i+1)]["genres"]
        for j in authors_ID: 
            matched = [s for s in arr_temp_a if Authors[j].lower() in s.lower()]
            if np.size(matched)!=0:
                authors_data_ID.append(i)
                break
        for j in utilities_ID: 
            matched = [s for s in arr_temp_u if Utilities[j].lower() in s.lower()]
            if np.size(matched)!=0:
                utilities_data_ID.append(i)
                break
        for j in genres_ID: 
            matched = [s for s in arr_temp_g if Genres[j].lower() in s.lower()]
            if np.size(matched)!=0:
                genres_data_ID.append(i)
                break


    return submit(submitted,str(rights_ID),str(genres_ID),str(utilities_ID),str(authors_ID),str(genres_data_ID),str(utilities_data_ID),str(authors_data_ID))


